import React from "react";
// import logo from './logo.svg';
//import * as tf from "@tensorflow/tfjs-core";
import * as handpose from "@tensorflow-models/handpose";
import Webcam from "react-webcam";
import "./App.css";
import { drawHand } from "./utilities";
import { sqrt, pow } from "mathjs"

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count : 0,
      rcount : 0,
      index_passed : 0,
      rotate_passed : 0,
      record : [],
      dist_array : [],
      ID : 0,
      real_time_inferencing:false,
      recording:false,
      button_mode:false,
      last_pressed:0,
      startAt: new Date(),
    };
    this.webcamRef = React.createRef(null);
    this.canvasRef = React.createRef(null);
    this.runHandpose = this.runHandpose.bind(this);
    this.stop_real_time_inference = this.stop_real_time_inference.bind(this);
    this.stop_record = this.stop_record.bind(this);
    this.record_video = this.record_video.bind(this);
    this.concat_frame = this.concat_frame.bind(this);
    this.inference = this.inference.bind(this);
    this.reset_counter = this.reset_counter.bind(this);
    this.increment_tap1 = this.increment_tap1.bind(this);
    this.increment_tap2 = this.increment_tap2.bind(this);
    this.switch_button = this.switch_button.bind(this);
  }

  switch_button(){
    if (this.state.button_mode === true){
      this.setState({button_mode:false})
    }
    else{
      this.setState({button_mode:true})
    }
  }

  increment_tap1(){
    if (this.state.last_pressed !== 1){
      this.setState({last_pressed:1})
      this.setState({count:this.state.count+1});
    }
  }

  increment_tap2(){
    if (this.state.last_pressed !== 2){
      this.setState({last_pressed:2})
      this.setState({count:this.state.count+1});
    }
  }

  norm(lm1, lm2){
    return sqrt(pow(lm1[0]-lm2[0], 2)+pow(lm1[1]-lm2[1], 2))
  }

  reset_counter(){
    this.setState({count:0});
    this.setState({rcount:0});
  }

  async runHandpose() {    
    require('@tensorflow/tfjs-backend-webgl');
    const net = await handpose.load();
    console.log("Handpose model loaded.");
    const Interval_ID = setInterval(() => {
      this.read_time_inference(net);
    }, 50);
    this.setState({ID:Interval_ID});
    this.setState({real_time_inferencing:true});
  };

  async read_time_inference(net) {
    if (
      typeof this.webcamRef.current !== "undefined" &&
      this.webcamRef.current !== null &&
      this.webcamRef.current.video.readyState === 4
    ) {
      const video = this.webcamRef.current.video;
      const videoWidth = this.webcamRef.current.video.videoWidth;
      const videoHeight = this.webcamRef.current.video.videoHeight;
      this.webcamRef.current.video.width = videoWidth;
      this.webcamRef.current.video.height = videoHeight;
      this.canvasRef.current.width = videoWidth;
      this.canvasRef.current.height = videoHeight;
      const hand = await net.estimateHands(video);
      if (hand.length > 0){
        hand.forEach((prediction) => {
          const landmarks = prediction.landmarks

          let pawn_dist = this.norm(landmarks[0], landmarks[2]); //sqrt(pow(landmarks[0][0]-landmarks[2][0], 2)+pow(landmarks[0][1]-landmarks[2][1], 2))
          let index_dist = this.norm(landmarks[4], landmarks[8]); //sqrt(pow(landmarks[8][0]-landmarks[4][0], 2)+pow(landmarks[8][1]-landmarks[4][1], 2))
          index_dist = index_dist/pawn_dist
          let current_time = new Date();
          this.setState({dist_array:[...this.state.dist_array, current_time - this.state.startAt]});
          console.log(this.state.dist_array);

          if (index_dist >= 0.5){this.setState({index_passed:1})}
          if (index_dist < 0.5 && this.state.index_passed === 1){
            this.setState({index_passed:0});
            this.setState({count:this.state.count+1});
          }

          let rotate_dist = (landmarks[2][0] - landmarks[17][0]) / pawn_dist;
          if (this.state.rotate_passed === 0){
            if (rotate_dist >= 0.5) this.setState({rotate_passed:1});
            if (rotate_dist <= -0.5) this.setState({rotate_passed:-1});
          }
          if (this.state.rotate_passed === 1 && rotate_dist <= -0.5){
            this.setState({rotate_passed:-1});
          }
          if (this.state.rotate_passed === -1 && rotate_dist >= 0.5){
            this.setState({rotate_passed:1});
            this.setState({rcount:this.state.rcount+1});
          }

          //console.log("passed:", this.state.passed, "count:", this.state.count)
          const ctx = this.canvasRef.current.getContext("2d");
          this.setState({context:ctx});
          drawHand(hand, ctx);
        });
      }      
    }
  };

  stop_real_time_inference() {
    clearInterval(this.state.ID);
    this.setState({real_time_inferencing:false});
  }

  async record_video(){    
    const Interval_ID = setInterval(() => {
      this.concat_frame();
    }, 50);
    this.setState({ID:Interval_ID});
    this.setState({recording:true});
  }

  async concat_frame() {
    if (
      typeof this.webcamRef.current !== "undefined" &&
      this.webcamRef.current !== null &&
      this.webcamRef.current.video.readyState === 4
    ) {
      const image = this.webcamRef.current.getScreenshot();      
      var img = document.createElement("img");
      img.onload = function(){
        this.setState({record:[...this.state.record, img]});
        //console.log(this.state.record, this.state.recording);
      }.bind(this)
      img.src = image;
    }
  }

  async inference() {
    require('@tensorflow/tfjs-backend-webgl');
    const net = await handpose.load();
    console.log("Handpose model loaded.");
    var i;
    for (i = 0; i<this.state.record.length; i++){
      const hand = await net.estimateHands(this.state.record[i]);
      console.log(hand);
      if (hand.length > 0){
        hand.forEach((prediction) => {
          const landmarks = prediction.landmarks

          let pawn_dist = this.norm(landmarks[0], landmarks[2]); //sqrt(pow(landmarks[0][0]-landmarks[2][0], 2)+pow(landmarks[0][1]-landmarks[2][1], 2))
          let index_dist = this.norm(landmarks[4], landmarks[8]); //sqrt(pow(landmarks[8][0]-landmarks[4][0], 2)+pow(landmarks[8][1]-landmarks[4][1], 2))
          index_dist = index_dist/pawn_dist          
          if (index_dist >= 0.5){this.setState({index_passed:1})}
          if (index_dist < 0.5 && this.state.index_passed === 1){
            this.setState({index_passed:0});
            this.setState({count:this.state.count+1});
          }

          let rotate_dist = (landmarks[2][0] - landmarks[17][0]) / pawn_dist;
          if (this.state.rotate_passed === 0){
            if (rotate_dist >= 0.5) this.setState({rotate_passed:1});
            if (rotate_dist <= -0.5) this.setState({rotate_passed:-1});
          }
          if (this.state.rotate_passed === 1 && rotate_dist <= -0.5){
            this.setState({rotate_passed:-1});
          }
          if (this.state.rotate_passed === -1 && rotate_dist >= 0.5){
            this.setState({rotate_passed:1});
            this.setState({rcount:this.state.rcount+1});
          }
        });
      }
    }
    this.setState({record:[]});
  }

  stop_record() {
    clearInterval(this.state.ID);
    this.inference();
    this.setState({recording:false});
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
        <Webcam
          ref={this.webcamRef}
          style={{
            position: "absolute",
            marginLeft: "auto",
            marginRight: "auto",
            left: 0,
            right: 0,
            textAlign: "center",
            zindex: 9,
            width: "auto",
            height: "auto",
          }}
        />
        <canvas
          ref={this.canvasRef}
          style={{
            position: "absolute",
            marginLeft: "auto",
            marginRight: "auto",
            left: 0,
            right: 0,
            textAlign: "center",
            zindex: 9,
            width: "auto",
            height: "auto",
          }}
        />
        </header>
          {this.state.button_mode ? (
            <div>
              <button onClick={this.increment_tap1} id="button1">Index Finger</button>
              <button onClick={this.increment_tap2} id="button2">Middle Finger</button>
            </div>
          ) : (
            <div></div>
          )}     
          {this.state.real_time_inferencing ? (
          <button onClick={this.stop_real_time_inference}>Stopping Real Time Inference</button>
          ) : (
          <button onClick={this.runHandpose}>Starting Real Time Inference</button>
          )}
          {this.state.recording ? (
          <button onClick={this.stop_record}>Stopping Recording</button>
          ) : (
          <button onClick={this.record_video}>Starting Recording</button>
          )}
          <button onClick={this.reset_counter}>Reset Counter</button>
          <button onClick={this.switch_button}>Switch On/Off Button</button>       
          <h4>Finger Tapping Count: {this.state.count}</h4>
          <h4>Rotate Count: {this.state.rcount}</h4>
      </div>
    );
  }
}

export default App;
